var status = {fill:"red",shape:"dot",text: "payload "};
var tableIndex = env.get("tableIndex") || "$topic";
var tableDataProp = env.get("tableDataProp") || "row";
var tableContext = env.get("tableContext");
var dashboard = env.get("dashboard");
if (!tableContext.hasOwnProperty("tableData") || !tableContext.hasOwnProperty("tableConfig")) {
    status.text="tableContext not defined";
    node.error(status.text);
    return [{payload:status},null];
}

// context store to cache table data (memoryOnly prefered)
var tableData = flow.get("$parent."+tableContext.tableData.name,tableContext.tableData.storage);
if (tableData===undefined) {
    node.warn("[ui-table handler] tableData initialized!");
    tableData={};
    flow.set("$parent."+tableContext.tableData.name,tableData,tableContext.tableData.storage);
}

// context Store to save table configuration (file)
var tableConfig = flow.get("$parent."+tableContext.tableConfig.name,tableContext.tableConfig.storage);
if (tableConfig===undefined) {
    node.warn("[ui-table handler] tableConfig initialized!");
    tableConfig={ResponsiveLayout:true};
    flow.set("$parent."+tableContext.tableConfig.name,tableConfig,tableContext.tableConfig.storage);
}

// context Store to save table configuration (file)
var tableEdit;
if (tableContext.hasOwnProperty("tableEdit")) {
    tableEdit = flow.get("$parent."+tableContext.tableEdit.name,tableContext.tableEdit.storage);
    if (tableEdit===undefined) {
        node.warn("[ui-table handler] tableEdit initialized!");
        tableEdit={};
        flow.set("$parent."+tableContext.tableEdit.name,tableEdit,tableContext.tableEdit.storage);
    }
}

// function to merge partial data into existing table row
var mergeObject = function (destination, source, filter) {
    for (let currentSource in source) {
        if (source.hasOwnProperty(currentSource)) {
            if (filter!==undefined && tableEdit && tableEdit.hasOwnProperty(filter) && tableEdit[filter].hasOwnProperty(currentSource)) {
                destination[currentSource]= tableEdit[filter][currentSource];
                source[currentSource]=tableEdit[filter][currentSource];
            } else {
                destination[currentSource]= source[currentSource];
            }
        }    
    }
    return source;
};

// deep search for a column
var searchTabulatorColumn = function (columns,key,match) {
    var result;
    for (let column of columns) {
        if (column.hasOwnProperty("columns")) {
            result = searchTabulatorColumn(column.columns,key,match);
            if (result!==undefined) return result;
        } else if (column.hasOwnProperty(key) && column[key]===match) {
            return column;
        }
    }
};

// command message to update add or update data on ui-table
var msgToTable={};
msgToTable.payload={
    "command":"updateOrAddData",
    "arguments": [],
    "returnPromise": false
};

if (msg.hasOwnProperty(tableDataProp)) {
    // store data for later recover
    if (!msg.hasOwnProperty("topic")) { // check if index existst
        status.text="msg.topic not defined!";
        return [{payload:status},null];
    }
    if (!tableData.hasOwnProperty(msg.topic)){ // first seen
        tableData[msg.topic]={};
        if (tableEdit && tableEdit.hasOwnProperty(msg.topic)) { // table edits available!
            Object.keys(tableEdit[msg.topic]).forEach((key) => {
                msg[tableDataProp][key]=tableEdit[msg.topic][key];
                tableData[msg.topic][key]=tableEdit[msg.topic][key];
            })
        }
    }
    if (!tableData[msg.topic].hasOwnProperty(tableIndex)) tableData[msg.topic][tableIndex]=msg.topic;
    msg[tableDataProp]=mergeObject(tableData[msg.topic],msg[tableDataProp],msg.topic);
    msg[tableDataProp][tableIndex]=msg.topic;
    msgToTable.payload.arguments=[[msg[tableDataProp]]];
    status.fill="green";
    status.text=msg.topic+" updated";
    return [{payload:status},msgToTable,null];
} if (msg.payload==="connect" || (msg.payload==="change" && msg.name===dashboard) || (msg.hasOwnProperty("payload") && msg.payload.hasOwnProperty("command"))) { 
    if (!msg.hasOwnProperty("ui_control")) {
        msg.ui_control = env.get('tabulator');
        status.text+=" ui_control added";
    }
    //process commands
    //node.warn({"command":msg.payload.command,"msg":msg,"object":msg.payload.object})
    if (msg.payload.hasOwnProperty("command")) {
        status.fill="blue";
        switch(msg.payload.command) {
            case 'deleteTable':
                flow.set("$parent."+tableContext.tableData.name,undefined,tableContext.tableData.storage);
                tableData={};
                status.text="tabledata deleted";
                node.warn("[ui-table handler] "+"tabledata deleted");
                break;
            case 'deleteDevice':
                if (tableData.hasOwnProperty(msg.payload.object)) {
                    delete tableData[msg.payload.object];
                    status.text=msg.payload.object+" deleted";
                } else {
                    status.fill="yellow";
                    status.text=msg.payload.object+" undefined";
                }
                break;
            case 'ignoreDevice':
                if (tableData.hasOwnProperty(msg.payload.object)) {
                    delete tableData[msg.payload.object];
                    status.text=msg.payload.object+" will be ignored";
                    if (!tableConfig.hasOwnProperty('ignoreDevice')) tableConfig.ignoreDevice={};
                    tableConfig.ignoreDevice[msg.payload.object]=true;
                }
                break;
            case 'unIgnoreDevice':
                if (tableConfig.hasOwnProperty('ignoreDevice')) {
                    delete tableConfig.ignoreDevice[msg.payload.object];
                }
                break;
            case 'unIgnoreDevices':
                delete tableConfig.ignoreDevice;
                break;
            case 'updateData':
                status.text="column "+msg.payload.column+" updated";
                return [{payload:status},msg];
            case 'updateTable':
                status.text=msg.payload.command+": ";
                break;
            case 'columnHide':
                if (!tableConfig.hasOwnProperty('columnVisible')) tableConfig.columnVisible={};
                tableConfig.columnVisible[msg.payload.object]=false;
                break;
            case 'columnUnHide':
                if (!tableConfig.hasOwnProperty('columnVisible')) tableConfig.columnVisible={};
                tableConfig.columnVisible[msg.payload.object]=true;
                break;
            case 'columnsUnHide':
                for (let column in tableConfig.columnVisible) {
                    if (tableConfig.columnVisible.hasOwnProperty(column)) tableConfig.columnVisible[column]=true;
                }
                break;
            case 'refreshTable':
                break;
            case 'restoreColumnOrder':
                delete tableConfig.columns;
                break;
            case 'resetColumnWidth':
                delete tableConfig.columnWidths;
                break;
            case 'setResponsiveLayout':
                tableConfig.ResponsiveLayout=!tableConfig.ResponsiveLayout;
                break;
            default:
                status.fill="red";
                status.text="unknown command "+msg.payload.command;
                node.warn("[ui-table handler] "+status.text);
                break;
        }
        flow.set("$parent."+tableContext.tableConfig.name,tableConfig,tableContext.tableConfig.storage);
        node.send([{payload:status},null,null]);
    }

    // crawl through tabulator arrays and updated user defined values
    var crawlTabulator = function (columns,match,config,property) {
        for (let column of columns) {
            if (column.hasOwnProperty("columns")) {
                crawlTabulator(column.columns,match,config,property);
            } else if (config.hasOwnProperty(column[match])) column[property]=config[column.field];
        }
    };

    // restore custom column width
    if (tableConfig.hasOwnProperty("columnWidths") && msg.hasOwnProperty("ui_control")) {
        crawlTabulator(msg.ui_control.tabulator.columns,"field",tableConfig.columnWidths,"width");
    }

    // restore custom column hide/show
    if (tableConfig.hasOwnProperty("columnVisible") && msg.hasOwnProperty("ui_control")) {
        crawlTabulator(msg.ui_control.tabulator.columns,"field",tableConfig.columnVisible,"visible");
    }

    // restore custom responsive / standard view
    if (tableConfig.hasOwnProperty("ResponsiveLayout")) {
        if (!tableConfig.ResponsiveLayout) {
            msg.ui_control.tabulator.responsiveLayout=false;
        }
        msg.ui_control.tabulator.columns.forEach((column,index) => {
            if (column.formatter==="responsiveCollapse") { // hide expand column on any position
                column.visible=tableConfig.ResponsiveLayout;
                return;
            }
        });
    }

    // sort columns
    if (tableConfig.hasOwnProperty("columns") && msg.hasOwnProperty("ui_control") && msg.ui_control.hasOwnProperty("tabulator")) {
        var addedColumns = 0;
        var sortColumnsByLayout = function (sortColumns, columnsLayout, targetColumns) {
            for (var layoutColumn=0;  layoutColumn<columnsLayout.length; layoutColumn++) {
                for (var sortColumn in sortColumns) {
                    if (sortColumns[sortColumn].hasOwnProperty("columns")) {
                        targetColumns.push({"title":sortColumns[sortColumn].title, "columns":[]});
                        sortColumnsByLayout(sortColumns[sortColumn].columns,columnsLayout,targetColumns[targetColumns.length-1].columns);
                        layoutColumn=addedColumns; // jump forward after childes added
                    } else {
                        if (columnsLayout[layoutColumn].field===sortColumns[sortColumn].field){
                            targetColumns.push(sortColumns[sortColumn]);
                            addedColumns++;
                            break;
                        }
                    }
                }
            }
        };                 
        var newColumns=[];
        sortColumnsByLayout(msg.ui_control.tabulator.columns,tableConfig.columns,newColumns);
        msg.ui_control.tabulator.columns=newColumns;
    }

    // restore stored lines after connect
    msg.payload=[];
    for (let device in tableData) {
        if (tableConfig && tableConfig.hasOwnProperty("ignoreDevice") && tableConfig.ignoreDevice[device]) {
            continue;
        }
        // merge edits into table
        if (tableEdit && tableEdit.hasOwnProperty(device)) {
            let tableRow = RED.util.cloneMessage(tableData[device]);
            Object.keys(tableEdit[device]).forEach((field) => {
                tableRow[field]=tableEdit[device][field];
            });
            msg.payload.push(tableRow);
        } else {
            msg.payload.push(tableData[device]);
        }
    }

    status.fill="blue";
    status.text+=" "+msg.payload.length+" rows restored";
    return [{payload:status},msg];
} if (msg.hasOwnProperty("ui_control")) {
    // callback from tabulator
    status.fill="blue";
    status.text="unknown callback "+msg.ui_control.callback;
    switch(msg.ui_control.callback) {
        case "columnResized": // save new column width
            if (tableConfig.columnWidths===undefined) tableConfig.columnWidths={};
            tableConfig.columnWidths[msg.ui_control.columnWidths.field]=msg.ui_control.columnWidths.width;
            flow.set("$parent."+tableContext.tableConfig.name,tableConfig,tableContext.tableConfig.storage);
            status.text=msg.ui_control.columnWidths.field+"="+msg.ui_control.columnWidths.width+"px";
            break;
        case "columnMoved": // save new column order
            if (tableConfig.columns===undefined) tableConfig.columns=[];
            tableConfig.columns=msg.ui_control.columns;
            flow.set("$parent."+tableContext.tableConfig.name,tableConfig,tableContext.tableConfig.storage);
            status.text="new column order";
            break;
        case "cellEdited":
            if (tableEdit) {
                if (!tableEdit.hasOwnProperty(msg[tableIndex])) tableEdit[msg[tableIndex]]={};
                tableEdit[msg[tableIndex]][msg.field] = msg.payload; // save data and mark es edited field
                flow.set("$parent."+tableContext.tableEdit.name,tableEdit,tableContext.tableEdit.storage);
                status.text=msg[tableIndex]+" "+msg.field+" edited to "+msg.payload;
                msg[tableDataProp]={};
                msg[tableDataProp][tableIndex]=msg[tableIndex];
                msg[tableDataProp][msg.field]=msg.payload;
                msgToTable.payload.arguments=[[msg[tableDataProp]]];
                node.send([{payload:status},msgToTable,msg]);
            } else {
                node.error("[ui-table handler] no tableEdit store defined!")
            }
            break;
        case "rowContext":
            msg.ignoredDevices=[];
            for (let device in tableConfig.ignoreDevice) {
                if (tableConfig.ignoreDevice.hasOwnProperty(device)) {
                    msg.ignoredDevices.push({"text":device,"icon":"fa fa-plug","topic":"unIgnoreDevice","payload":device})  
                }
            }
            break;
        case "headerContext":
            msg.hiddenColumns=[];
            let tabulatorConfig = env.get('tabulator');
            for (let column in tableConfig.columnVisible) {
                if (tableConfig.columnVisible.hasOwnProperty(column) &&
                    !tableConfig.columnVisible[column]) {
                    let configColumn=searchTabulatorColumn(tabulatorConfig.tabulator.columns,"field",column);
                    let icon;
                    if (configColumn.hasOwnProperty('title') && configColumn.title.toLowerCase().includes('</i>')) {
                        // <i class='fa fa-star-half-o'></i> State
                        let start=configColumn.title.indexOf("'fa ");
                        let end=configColumn.title.indexOf("'",start+1);
                        icon=configColumn.title.substring(start+4,end);
                    }
                    msg.hiddenColumns.push({"text":column,"icon":icon,"topic":"columnUnHide","payload":configColumn.field})  
                }
            }
            break;
        default:
            // if rowIndex exists pass complete object
            if (msg.hasOwnProperty(tableIndex)) {
                msg.rowData=tableData[msg[tableIndex]];
            }
            status.text="pass message";
    }
    return [{payload:status},null,msg];
}
// nothing to do bejond this point
