msg.requestid = msg._msgid;
msg.payload = msg.payload.slice(0, msg.payload.length-2);
return msg;

let id = flow.get('lastID'); //read out the last assigned request ID from the flow memory area
let command = msg.payload; //read the passed Ventuz Commands

if(typeof id == 'undefined'){ //If there is no request ID, start with 0
    id = 0;
}else if(id == 2147483647){ //If the highest possible value of a request ID is reached, start again at 0
    id = 0;
}
let info = {msgID:msg._msgid, timestamp:Date.now()};

if(msg.topic !== undefined){
    info['sourceTopic'] = msg.topic;
}else{
    info['sourceTopic'] = undefined;
}

if(typeof command.Command !== 'undefined'){
    info['command'] = command.Command;
}

id++; //increment the request ID

//create a message payload for Ventuz under the new request ID
command.RequestID = id;
let message = {payload:command};
let flowId = 'RequestID_'+id.toString();

collectGarbage(); //delete stored Request Data rubbish

//store the new request ID in the flow memory area
flow.set('lastID',id);
flow.set(flowId, info);

//send the message
return message;

//if saved requests grow up to 1000 items then lookup for items which are more than 10 minutes old and set them ready for garbage collection
function collectGarbage(){
    let keys = flow.keys();
    if(keys.length > 1000){
        for(let i=0;i<keys.length;i++){
            let info = flow.get(keys[i]);
            if(info.timestamp !== undefined){
                let timeDiff = Date.now() - info.timestamp;
                if(timeDiff > 600000){
                    flow.set(keys[i], undefined);
                }
            }
        }
    }
}

let responseMsg = msg;
responseMsg.req = {};//request object
try{
    let flowID = 'RequestID_'+responseMsg.payload.RequestID.toString(); //get Request_ID
    let info = flow.get(flowID); //get stored Request Data
    responseMsg.req.command = info['command']; //fill request object
    responseMsg.req.msgid = info['msgID'];
    responseMsg.req.topic = info['sourceTopic'];
    flow.set(flowID, undefined);//set stored Request Data ready for garbage collection
}catch(err){
    responseMsg.req.command = undefined; //on Error set request object fields to undefined
    responseMsg.req.msgid = undefined;
    responseMsg.req.topic = undefined;
}finally{
    return responseMsg;
}

