body {
	width: 100%;
	height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	overflow: hidden;
	margin: 0;
	background-color: #e3efe8;
	background-image: linear-gradient(315deg, #e3efe8 0%, #96a7cf 74%);
}


nav.menu {
	display: flex;
	justify-content: space-between;
	position: relative;
	height: 150px;
	padding: 0 29px 10px;
	background: #fff0;
	align-items: flex-end;
	width: 700px;
}

nav.menu:before {
	content: "";
	width: 100%;
	height: 150px;
	background: #181818;
	position: absolute;
	left: 0;
	bottom: 0;
	border-radius: 20px;
	z-index: -1;
	box-shadow: 1px 1px 2px 0px #fff;
}

input { display: none; }

.break {
  flex-basis: 100%;
  height: 0;
}

label {
	text-decoration: none;
	font-family: sans-serif;
	text-transform: uppercase;
	font-size: 14px;
	min-width: 100px;
	height: 100px;
	margin: 10px 10px 20px;
	text-align: center;
	display: inline-grid;
	align-items: end;
	color: #b0bfd8;
	position: relative;
	transition: all 0.5s cubic-bezier(0.68, -0.55, 0.27, 1.55) 0s;
	cursor: pointer;
}

label:hover {
	color: #fff;
	text-shadow: 0 0 5px #fff, 0 0 10px #fff;
}

input:checked + label {
	color: #fff;
	height: 130px;
}

.selector {
	--hole: #2196f3;
	background: 
		radial-gradient(circle at 50% 50%, #fff8 30px, #fff0 45px, #fff 50px, #fff0 50px 100%), 
		radial-gradient(circle at 50% 50%, var(--hole) 0 45px, #fff0 50px 100%),
		radial-gradient(circle at 50% 75px, #181818 0 70px, #fff0 71px 100%);
